(function($, game, Field) {


    var $board = $("#game-board");
    var board = $board.get(0);
    var currentZoom = 2;
    var rowsToFetch = 5;

    var convertEmToPixel = function( value, scope, suffix ) {
	    if (!scope || value.toLowerCase().indexOf("rem") >= 0) {
	      scope = 'body';
	    }
	    if (suffix === true) {
	      suffix = 'px';
	    } else {
	      suffix = null;
	    }
	    var multiplier = parseFloat(value);
	    var scopeTest = $('<div style="display: none; font-size: 1em; margin: 0; padding:0; height: auto; line-height: 1; border:0;">&nbsp;</div>').appendTo(scope);
	    var scopeVal = scopeTest.height();
	    scopeTest.remove();
	    return Math.round(multiplier * scopeVal) + suffix;
    };

	var GUI = function() {
		this.game = game;
        this.board.GUI = this;
		this.board.event.board = this.board;
		return this;
	};

	GUI.prototype.board = {
		el : board,
		$el : $board
	};

	GUI.prototype.board.load = function  (data, done) {
		var self = this;
		if (data){
        	this.GUI.grid.state.rows = data;
		}
        this.zoom(this.zoom());

		$board.one('transitionend', ".hex", function(e) {
            board.scrollTop = (board.scrollHeight/4)+1;
            board.scrollLeft = (board.scrollWidth/4)+1;
            if (done){
            	done();

            	var touchDevice = ('ontouchstart' in window);
            	if (!touchDevice){
            	    window.setTimeout(function() {
            	        $board.overscroll({
            	            showThumbs : false
            	        });
            	    }, 800);
            	 };
            	 $("#game-loading-progress").hide();
            }
        });

        this.GUI.grid.setState({rows: this.GUI.grid.state.rows}, function  () {
			self.$el.show();
            board.scrollTop = (board.scrollHeight/4)+1;
            board.scrollLeft = (board.scrollWidth/4)+1;
        });
    };

	GUI.prototype.board.neededHexTilesToOverfillScreen = function() {
		var px = convertEmToPixel(this.zoom());
		return {
			colCount : $board.width() / px + 5,
			rowCount : $board.height() / px + 5,
		};
	};

    GUI.prototype.board.zoom = function(scale) {
        if (scale){
            $board.one('transitionend', ".hex", function(e) {
                board.scrollTop = (board.scrollHeight/4)+1;
                board.scrollLeft = (board.scrollWidth/4)+1;
            });

            game.style.setCss(scale);
            currentZoom = scale;
        } else {
            return currentZoom;
        }
    };

    GUI.prototype.board.event = {};

	GUI.prototype.board.event.scroll = function (event) {
        var trueDivHeight = board.scrollHeight;
        var maxScollTop = trueDivHeight - $board.height();

        if (board.scrollTop === maxScollTop){
            game.GUI.board.event.scrolltBottom();
        }
        if (board.scrollTop === 0){
            game.GUI.board.event.scrollTop();
        }
    };


    GUI.prototype.board.event.scrolltBottom = function() {
        var self = this;
        this.board.el.removeEventListener('scroll', self.scroll);
        var bottomHex = $(".hex:first");

        var rows = this.board.GUI.grid.state.rows;
        var ordinate = {
            x1 : rows[rows.length-1][0].position.x+1,
            y1 : rows[rows.length-1][0].position.y
        };
        ordinate.x2 = ordinate.x1 + rowsToFetch;
        ordinate.y2 = ordinate.y1 + rows[rows.length-1].length;
        self.board.fetchData(ordinate, function(rows) {
            for (var r = 0; r < rows.length; r++) {
                self.board.GUI.grid.state.rows.shift();
                self.board.GUI.grid.state.rows.push(rows[r]);
            }
            self.board.GUI.grid.setState({rows: self.board.GUI.grid.state.rows}, function() {
                board.scrollTop -= bottomHex.height() * (rowsToFetch - 1)+1;
                board.addEventListener('scroll', self.scroll);
            });
        });
    };

    GUI.prototype.board.event.scrollTop = function() {
        var self = this;
        this.board.el.removeEventListener('scroll', self.scroll);
        var bottomHex = $(".hex:first");

        var rows = this.board.GUI.grid.state.rows;
        var ordinate = {
            x1 : rows[0][0].position.x-(rowsToFetch),
            y1 : rows[0][0].position.y
        };
        ordinate.x2 = ordinate.x1 + rowsToFetch;
        ordinate.y2 = ordinate.y1 + rows[0].length;
        self.board.fetchData(ordinate, function(rows) {
            for (var r = rows.length - 1; r >= 0; r--) {
                self.board.GUI.grid.state.rows.pop();
                self.board.GUI.grid.state.rows.unshift(rows[r]);
            }
            self.board.GUI.grid.setState({rows: self.board.GUI.grid.state.rows}, function() {
                board.scrollTop += bottomHex.height() * (rowsToFetch - 1)-1;
                board.addEventListener('scroll', self.scroll);
            });
        });
    };

    GUI.prototype.board.fetchData = function  (ordinate, onComplete) {
        var self = this;
        this.GUI.$loading.show();
        var rows = [];

        for (var x = ordinate.x1; x < ordinate.x2; x++) {
            var row = [];
            for (var y = ordinate.y1; y < ordinate.y2; y++) {
              var fieldParms = {
                position : {
                    x : x,
                    y : y
                }
              };
              row.push(new Field(fieldParms));
            }
            rows.push(row);
        }

        window.setTimeout((function() {
            onComplete(rows);
            self.GUI.$loading.hide();
        }), 800);
    };

    
    GUI.prototype.board.viewPoint = {
        x:-110,y:-0
    };

    GUI.prototype.board.reload = function(){
        var self = this;
        this.$el.hide();
        self.GUI.$loading.show();
        this.el.removeEventListener('scroll', this.event.scroll);
        var needed = this.neededHexTilesToOverfillScreen();
        var viewPoint = self.viewPoint;

        var ordinate = {
            x1 : Math.round(viewPoint.x - (needed.rowCount/2)),
            x2 : Math.round(viewPoint.x + (needed.rowCount/2)),
            y1 : Math.round(viewPoint.y - (needed.colCount/2)),
            y2 : Math.round(viewPoint.y + (needed.colCount/2))
        };

        self.fetchData(ordinate, function(mockupFieldData) {
            self.load(mockupFieldData, function() {
                    self.el.addEventListener('scroll', self.event.scroll);
            });
        });
    };

    GUI.prototype.$loading = $('<div id="game-loading-progress"><img src="data:image/gif;base64,"></div>');
    GUI.prototype.board.init = function() {
        var self = this;
        var rl = function() {
            self.reload();
        };
        self.GUI.$loading.insertAfter(this.$el);
        $("body").on("game-components-ready",rl);
        $(window).resize(rl);
    };

	game.GUI = new GUI();
    game.GUI.board.init();

})(window.jQuery, window.game, window.game.class.Field);